insert into e_square_pos(customer_id, merchant_id, authorization_code, application_id, application_secret, access_token, refresh_token, access_token_timestamp, access_token_expiry) values((select id from e_customer where code = ${customerCode} limit 1), ${merchant_id}, ${authorization_code}, ${application_id}, ${application_secret}, ${access_token}, ${refresh_token}, now(), ${access_token_expiry}) ON CONFLICT(merchant_id) DO UPDATE set customer_id = (select id from e_customer where code = ${customerCode} limit 1), authorization_code = ${authorization_code}, last_modified_date = now(), access_token = ${access_token}, access_token_expiry = ${access_token_expiry};